import * as bunyan from "bunyan";
import Bunyan2Loggly = require("bunyan-loggly");

export class BunyanLoggerFactory {

    public createLogger(options) {

        return bunyan.createLogger({

            name: options.name,
            streams: [
                {
                    level: "debug",
                    type: "raw",
                    stream: new Bunyan2Loggly({
                        token: options.token,
                        subdomain: options.subdomain
                    }, 1)
                }
            ]

        });

    }

}
