import * as StatsDClient from "statsd-client";

export class StatsDClientFactory {

    public createClient(settings) {

        this.validate(settings);

        return new StatsDClient({host: settings.host, port: settings.port});

    }

    private validate(settings: any) {

        if (!settings) {

            throw new Error("Missing settings for StatsD connector");

        }

        if (!settings.port) {

            throw new Error("Missing port number");

        }

        if (!settings.host) {

            throw new Error("Missing host name");

        }

    }

}
